mapper.update:
update member
set name=#{profileDto.name},
    intro=#{profileDto.intro},
    profile_image=#{profileDto.profileImage},
    modified_at=#{modifiedAt}
where member_id = #{memberId}

update member
set password=#{password},
    modified_at=#{modifiedAt}
where member_id = #{memberId}

update member
set is_deleted=true,
    modified_at=#{modifiedAt}
where member_id = #{memberId}

mapper.select:
select member_id, email, password, name, created_at,modified_at, profile_image, intro, is_deleted
from member
where member_id = #{memberId}

select member_id, email, password, name, created_at,modified_at, profile_image, intro, is_deleted
from member
where email = #{email}
