mapper.update:
update member
set name=#{profileDto.name},
    intro=#{profileDto.intro},
    profile_image=#{profileDto.profileImage},
    modified_at=#{modifiedAt}
where member_id = #{memberId}

update member
set password=#{password},
    modified_at=#{modifiedAt}
where member_id = #{memberId}

update member
set is_deleted=true,
    modified_at=#{modifiedAt}
where member_id = #{memberId}

mapper.select:
select * from member
where member_id in

    #{id}
 and is_Deleted = false;
